function last_frame = fw_spectrum(dcam,x);% FW_PREVIEW Preview a DCAM camera.%%   last_frame = fw_preview(dcam,n) previews a digital camera dcam for n%   frames, and returns the the last recorded frame.  The camera must be%   initialized using fw_init first.%%%% Options%%% Code starts herefigure('WindowButtonDownFcn',@click_callback);global no_click;no_click = true;nm = 1e-9;n = 0;while no_click    n = n+1;    spec = sum(fw_snap(dcam));    plot(x,Norm(spec));    endlast_frame = this_frame;clear this_frame;endfunction click_callback(src,eventdata)global no_click;disp('Stopping the camera...');no_click = false;end